SELECT MGR_SSN , COUNT(*)  FROM (SELECT * FROM DEPARTMENT , (SELECT * FROM PROJECT WHERE DNUM IN (SELECT DNUM FROM PROJECT WHERE PNAME="PRODUCTY")) AS M WHERE DNUM=DNUMBER) AS X GROUP BY MGR_SSN;
SELECT * FROM DEPARTMENT , (SELECT * FROM PROJECT WHERE DNUM IN (SELECT DNUM FROM PROJECT WHERE PNAME="PRODUCTY")) AS M WHERE DNUM=DNUMBER;
SELECT * FROM PROJECT WHERE DNUM IN (SELECT DNUM FROM PROJECT WHERE PNAME="PRODUCTY");